mapper.update:
update admin set
total_cash = total_cash + #{total_cash},
total_charge = total_charge + #{charge, jdbcType=BIGINT}, 
total_exchange = total_exchange + #{exchange, jdbcType=BIGINT},
total_betting = total_betting + #{betting, jdbcType=BIGINT},
total_win = total_win + #{win, jdbcType=BIGINT},
total_lose = total_lose + #{total_lose, jdbcType=BIGINT}
total_salary = total_salary + #{salary},
salary = salary + #{salary}
where id=#{id}

update admin set
dividend = #{dividend}
where id=#{id}
